<!DOCTYPE html><html lang="eng"><head> <meta charset="UTF-8"> <meta name="viewport" content="width=device-width, initial-scale=1.0"> <title>Countdown</title> <style media="screen"> @import 'https://fonts.googleapis.com/css?family=Playfair+Display';* {margin: 0;}body {font-family: 'Playfair Display', serif;background: #f5f5f5;overflow-x: hidden;}.wrap {position: absolute;left: 0; right: 0; top: 50%;display: block;margin: -140px auto 0;padding: 20px 0;background: #fff;/* border: 1px solid #eee;*/width: 100%;max-width: 400px;height: 200px;border-radius: 3px;text-align: center;box-shadow: 0 0 2px 0 rgba(0, 0, 0, 0.1), 0 2px 4px 0 rgba(0, 0, 0, 0.1);animation: fadeIn 1.25s ease-in-out;}@keyframes fadeIn {0%, 70% {opacity: 0;}70% {top: 52%;opacity: 0;}100% {opacity: 1;}}.wrap img {height: 100px;margin-top: 5px;}.wrap h2 {margin: 0 0 10px;font-weight: 100;color: #555;}.wrap h3 {color: #359b8e;font-size: 1.4rem;}.wrap h3 span {color: #555;font-size: 0.8rem;} </style></head><body> <div class="wrap"> <img src="https://bsweet.today/wp-content/uploads/2016/05/logo.png" alt="B.Sweet logo" /> <h2>Coming Soon</h2> <h3 id="countdown"></h3> </div> <script> var end = new Date('09/26/2016 11:20 AM'); var _second = 1000; var _minute = _second * 60; var _hour = _minute * 60; var _day = _hour * 24; var timer; function showRemaining() { var now = new Date(); var distance = end - now; if (distance < 0) { clearInterval(timer); document.getElementById('countdown').innerHTML = 'expired'; return; } var days = Math.floor(distance / _day); var hours = Math.floor((distance % _day) / _hour); var minutes = Math.floor((distance % _hour) / _minute); var seconds = Math.floor((distance % _minute) / _second); document.getElementById('countdown').innerHTML = days + ' <span>days</span> '; document.getElementById('countdown').innerHTML += hours + ' <span>hrs</span> '; document.getElementById('countdown').innerHTML += minutes + ' <span>min</span> '; document.getElementById('countdown').innerHTML += seconds + ' <span>sec</span>'; } timer = setInterval(showRemaining, 1000); </script></body></html>
